import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ListOfGoals } from "../ApiManager";

export const YearInReview = () => {
  const [goals, setGoalList] = useState([]);

  const getFilteredGoals = () => {
    ListOfGoals().then((data) => {
      setGoalList(
        data.filter((item) => {
          return item.userId === parseInt(localStorage.getItem("goal_keeper"));
        })
      );
    });
  };

  useEffect(() => {
    getFilteredGoals();
  }, []);

  const deleteGoal = (id) => {
    fetch(`http://localhost:8088/goals/${id}`, {
      method: "DELETE",
    }).then(() => {
      getFilteredGoals();
    });
  };

  return (
    <>
      {goals.map((goalObj) => {
        return (
          <>
            <div key={goalObj.id}>
              <Link key={goalObj.id} to={`/goals/${goalObj.id}`}>
                {goalObj.goalDescription}
              </Link>
              <button onClick={() => deleteGoal(goalObj.id)}>
                DELETE
              </button>
            </div>
            {
              <button
                className="drop-btn"
                style={{ background: `${goalObj.category?.color}` }}
              >
                {goalObj.category?.category}
              </button>
            }

            <button className="drop-btn">{goalObj.priority?.priority}</button>

            <button className="drop-btn">{goalObj.term?.term}</button>
          </>
        );
      })}
    </>
  );
};
