import React, { useState, useEffect } from "react";
import { getCategories, ListOfGoals } from "../../ApiManager";
import "./GoalList.css";
// import { format } from 'date-fns'
import { Link, useParams } from "react-router-dom";
import { Col, Container, Row } from "react-bootstrap";

export const GoalList = () => {
  const [goallist, setGoalList] = useState([]);
  const { goalsId } = useParams();
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    getCategories().then((data) => {
      setCategories(data);
    });
  }, []);

  useEffect(() => {
    ListOfGoals().then((data) => {
      setGoalList(data);
    });
  }, []);

  const unixTime = (time) => {
    const myDate = new Date(time);
    const myDateString = `${
      myDate.getDate() + 1
    }/${myDate.getMonth()}/${myDate.getFullYear()}`;
    return myDateString;
  };

  return (
    <>
      <h4 className="title-myGoals">My Goals</h4>

      {goallist.map((goal) => {
        return (
          <div key={goal.id} className="goal_list">
            <Container>
              <Row xs={6}>
                <Col md={12}>
                  <Link to={`/goals/${goal.id}`}>
                    Goal description: {goal.goalDescription}
                    Goal objectives: {goal.goalObjective}
                    Notes: {goal.goalNotes}
                    Start Date: {unixTime(goal.goalDate)}
                  </Link>
                  <div>
                  <button className="category">
                    {goal.category?.category}
                  </button>
                  </div>
<div>
                  <button className="priority">
                    {goal.priority?.priorityId}
                  </button>
</div>
                </Col>
              </Row>
            </Container>
          </div>
        );
      })}
    </>
  );
};
