import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom"
import { ListOfGoals } from "../ApiManager"


export const YearInReview = () => {
  const [goals, setGoalList] = useState([])

  useEffect(() => {
    ListOfGoals()
    .then((data) => {
      setGoalList(data);
      console.log(data);
    });
  },[]);

  const deleteTicket = (id) => {
    fetch(`http://localhost:8088/serviceTickets/${id}`, {
      method: "DELETE",
    })
    .then(() => {
        fetch(`http://localhost:8088/serviceTickets?_expand=customer&_expand=employee`)
        .then((res) => res.json())
        .then((data) => {
            updateTickets(data)
        })
    })
  }

  return (
  <>

        {
            goals.map((goalObj) => {
              return (
                    <div key={goalObj.id}>
                      <Link key={goalObj.id} to={`/goals/${goalObj.id}`}>
                      {goalObj.goalDescription} 
                      </Link>
                      <button onClick={deleteGoal(goalObj.id)}>
                        DELETE
                      </button>
                    </div>
                )
            })
        }

  </>

  );
};
