import { ListOfGoals } from "../ApiManager"

export const GoalList = ({message}) => {
  const [goallist, setGoalList] = useState([]);

      useEffect(
         () => {
            ListOfGoals()
            .then((data) => {
               setGoalList(data)
               console.log(data)
            })
         }, []
      )

  const unixTime = (time) => {
      const myDate = new Date(time)
       const myDateString = `${myDate.getMonth() + 1}/${myDate.getDate()}/${myDate.getFullYear()}`
       return myDateString
  }

  return (
     <>
     <h1>Goal List</h1>

      {
         goallist.map((goal) => {
            return (
               <div key={goal.id}>
                  <p>
                  {goal.goalDescription}{goal.goalNotes} {unixTime(goal.goalDate)}
                  {message}
                  <button onClick={() => {
                     setMessage("test string")
                  }}>
                     change message 
                  </button>
                     </p>
                     </div>
            )
         })
      }


    </>
  );
};
