import React, { useState, useEffect } from "react";
import { getCategories, ListOfGoals } from "../../ApiManager";
import "./GoalList.css";
// import { format } from 'date-fns'
import { Link, useParams } from "react-router-dom";
import { Col, Container, Row } from "react-bootstrap";

export const GoalList = () => {
  const [goallist, setGoalList] = useState([]);
  const { goalsId } = useParams();
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    getCategories().then((data) => {
      setCategories(data);
    });
  }, []);

  useEffect(() => {
    ListOfGoals().then((data) => {
      setGoalList(data);
    });
  }, []);

  const unixTime = (time) => {
    const myDate = new Date(time);
    const myDateString = `${
      myDate.getDate() + 1
    }/${myDate.getMonth()}/${myDate.getFullYear()}`;
    return myDateString;
  };

  return (
    <>
      <h4 className="title-myGoals">My Goals</h4>

      {goallist.map((goal) => {
        return (
          <div key={goal.id} className="goal_list">
            {/* <p className="goals"> */}
            <Container>
              <Row md={1}>
                <Col md={12}>
                  <Link to={`/goals/${goal.id}`}>
                    Goal description: {goal.goalDescription}
                    Goal objectives: {goal.goalObjective}
                    Notes: {goal.goalNotes}
                    Start Date: {unixTime(goal.goalDate)}
                  </Link>
                  {categories.map((catObj) => {
                    if (catObj.id === goal.categoryId) {
                      return (
                        <button className="category">{catObj.category}</button>
                      );
                    } else {
                      if (catObj.id === goal.priorityId) {
                        return (
                          <button className="priority">
                              {goal.priorityId}
                              </button>
                        )

                    }
                    }
                  })}

                </Col>
              </Row>
            </Container>
            {/* </p> */}
          </div>
        );
      })}
    </>
  );
};
