export const MyGoals = () => {
    useEffect(() => {
        ListOfGoals()
        .then((data) => {
          setGoalList(data);
        });
      }, []);

      const unixTime = (time) => {
        const myDate = new Date(time)
        const myDateString = `${myDate.getDate() + 1}/${myDate.getMonth()}/${myDate.getFullYear()}`;
        return myDateString;
      };

      return (
        <>
          <h4>My Goals</h4>

          {goallist.map((goal) => {
            return (
              <div key={goal.id} className="goal_list">
                 <p className="goals">
                Goal description: {goal.goalDescription}
                Goal objectives: {goal.goalObjective}
                Notes: {goal.goalNotes}
                Start Date: {unixTime(goal.goalDate)}
                 </p>
              </div>
            );
          })}
        </>
      );
    };
