import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap"
import { useParams } from "react-router-dom";
import { getGoal } from "../../ApiManager";
import "./GoalMilestone.css"

export const GoalMilestone = () => {
  const { goalsId } = useParams();
  const [goal, setGoal] = useState({})
  const [milestone, setMilestone] = useState([])

  useEffect(() => {
    getGoal(goalsId).then((data) => {
      setGoal(data);
    });
  }, []);

  return (
    <>
    <Container>

    <h3>Milstone Review</h3>
    <Container>
      <fieldset>
        <div className="form-group">
        <Row xs={4} md={2}>
        <Col xs={6}>
          <input type="checkbox"
        label="My Value"
        value={""}
        onChange={(event) => {
          setMilestone(event.target.checked)
        }}
      />
          <label className="title" htmlFor="name">
            Overall Direction
          </label>
          </Col>
          </Row>
        </div>
      </fieldset>
    </Container>
    <Container>
      <fieldset>
        <div className="form-group">
        <Col xs={6}>
          <input type="checkbox"
        label="My Value"
        value={""}
        onChange={(event) => {
          setMilestone(event.target.checked)
        }}
      />
          <label className="title" htmlFor="name">
            Flushed And Defined
          </label>
        </Col>
        </div>
      </fieldset>
    </Container>
    <Container>
      <fieldset>
        <div className="form-group">
        <Col xs={6}>
          <input type="checkbox"
        label="My Value"
        value={""}
        onChange={(event) => {
          setMilestone(event.target.checked)
        }}
      />
          <label className="title" htmlFor="name">
            Tangible Progress Made
          </label>
        </Col>
        </div>
      </fieldset>
    </Container>
    <Container>
      <fieldset>
        <div className="form-group">
        <Col xs={6}>
          <input type="checkbox"
        label="My Value"
        value={""}
        onChange={(event) => {
          setMilestone(event.target.checked)
        }}
      />
          <label className="title" htmlFor="name">
            Goal Objective Attained
          </label>
        </Col>
        </div>
      </fieldset>
    </Container>

    </Container>
    </>
  );
};
