import React, { useState,useEffect } from "react";
import { ListOfGoals } from "../ApiManager"

export const GoalList = ({message, setMessage}) => {
  const [goallist, setGoalList] = useState([]);

      useEffect(
         () => {
            ListOfGoals()
            .then((data) => {
               setGoalList(data)
               console.log(data)
            })
         }, []
      )

  const unixTime = (time) => {
      const myDate = new Date(time)
       const myDateString = `${myDate.getMonth() + 1}/${myDate.getDate()}/${myDate.getFullYear()}`
       return myDateString
  }

  return (
     <>
     <h1>Goal List</h1>

      {
         goallist.map((goal) => {
            return (
               <div key={goal.id}>
                 Goal description: {goal.goalDescription}
                  Goal name: {goal.goalObjective}
                  Notes: {goal.goalNotes} 
                  Start Date: {unixTime(goal.goalDate)}

                     </div>
            )
         })
      }


    </>
  );
};
