import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getGoal } from "../../ApiManager";
import "./GoalMilestone.css"

export const GoalMilestone = () => {
  const { goalsId } = useParams();
  const [goal, setGoal] = useState({});

  useEffect(() => {
    getGoal(goalsId).then((data) => {
      setGoal(data);
    });
  }, []);

  return (
    <>
    <h3>Milstone Review</h3>
      <fieldset>
        <div className="form-group">
          <label className="title" htmlFor="name">
            Overall Direction
          </label>
          <input type="checkbox"
        label="My Value"
        value={checked}
        onChange={handleChange}
      />

          {goal.goalDescription}
        </div>
      </fieldset>
      <fieldset>
        <div className="form-group">
          <label className="title" htmlFor="name">
            Flushed And Defined
          </label>
          {goal.goalDescription}
        </div>
      </fieldset>
      <fieldset>
        <div className="form-group">
          <label className="title" htmlFor="name">
            Tangible Progress Made
          </label>
          {goal.goalDescription}
        </div>
      </fieldset>
      <fieldset>
        <div className="form-group">
          <label className="title" htmlFor="name">
            Features Completed
          </label>
          {goal.goalDescription}
        </div>
      </fieldset>
      <fieldset>
        <div className="form-group">
          <label className="title" htmlFor="name">
            Goal Objectives Attained
          </label>
          {goal.goalDescription}
        </div>
      </fieldset>
    </>
  );
};
