import React, { useState, useEffect } from "react";

export const GoalMilestone = () => {

  const {goalsId} = useParams()
    const [goal, setGoal] = useState({})

    useEffect(() =>{
        getGoal(goalsId)
        .then((data) => {
            setGoal(data)
        })

    },[])

  return (
    <>
      <h1>Goal List</h1>


      <fieldset>
        <div className="form-group">
          <label htmlFor="name">Goal Milestone Check:</label>
          <input
            required
            autoFocus
            type="text"
            className="form-control"
            placeholder="Enter goal progress observations"
            onChange={(event) => {
              setGoalMilestone(event.target.checked);
            }}
          />
        </div>
      </fieldset>
    </>
  );
};
